package javapractice;

public class Lec2forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
//print 1 to 10
for(int i=1; i<10; i++) {
	System.out.println(i);
}
	
	// print 2 to 50
for(int i=2; i<=50; i++) {
	System.out.println(i);	
}
		
		
// print all even number 30 to 102		
	for (int i=30; i<=102;i=i+2) {
		System.out.println(i);
	}
		
		
//print all odd number 89 to 229
	for (int i=89; i<=229; i=i+2)
	{
		System.out.println(i);
	}
		
//use a for loop to print "Beautiful Bangladesh" 5 times
	for (int i=0; i<=5; i=i+1)
	{
		System.out.println("Beautiful Bangladesh");
	}
		
		
	// use for loop to print "this is month of November" 10 times
	
	for (int i=1; i<=10; i=i+1)
	{
		System.out.println("this is month of November");
	}
	
	// print 158 to 200
	 for (int i=158; i<=200; i++)
	 {
		 System.out.println(i);
	 }
	
	//print all prime number 3 to 25
	 for (int i=3; i<=25;i=i+2 )
	 {
		 System.out.println(i);
	 }
	
	
	
	
	
	
	
	
	
	
	
	
	

		
		
	}

}
