package javapractice;

public class Lec3ifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
//use if else condition for grading system
		int digit=80;
	    char grade;
	    
	    if (digit>=80) {
	    	grade='A';
	    }
	    
	    else if (digit>=60) {
	    	grade='B';
	    }
		
	    else if (digit>=50) {
	    	grade='C';
	    }
		
	    else {
	    grade ='F';	
	    }
		
		System.out.println("Your grade is "+ grade);
		
		//use if else condition for 
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
